function get_social_count_twitter(b,a){$.ajax({url:"http://urls.api.twitter.com/1/urls/count.json",dataType:"jsonp",data:{url:b},success:function(c){$(a).text(c.count||0);},error:function(){$(a).text("0");}});}function get_social_count_facebook(b,a){$.ajax({url:"https://graph.facebook.com/",dataType:"jsonp",data:{id:b},success:function(c){$(a).text(c.shares||0);},error:function(){$(a).text("0");}});}function get_social_count_googleplus(b,a){$.ajax({type:"get",dataType:"xml",url:"http://query.yahooapis.com/v1/public/yql",data:{q:"SELECT content FROM data.headers WHERE url='https://plusone.google.com/_/+1/fastbutton?hl=ja&url="+b+"' and ua='#Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.154 Safari/537.36'",format:"xml",env:"http://datatables.org/alltables.env"},success:function(f){var e=$(f).find("content").text();var c=e.match(/window\.__SSR[\s*]=[\s*]{c:[\s*](\d+)/i);var d=(c!==null)?c[1]:0;$(a).text(d);}});}function get_social_count_hatebu(b,a){$.ajax({url:"http://api.b.st-hatena.com/entry.count?callback=?",dataType:"jsonp",data:{url:b},success:function(c){$(a).text(c||0);},error:function(){$(a).text("0");}});}function get_social_count_pocket(b,a){$.ajax({type:"get",dataType:"xml",url:"http://query.yahooapis.com/v1/public/yql",data:{q:"SELECT content FROM data.headers WHERE url='https://widgets.getpocket.com/v1/button?label=pocket&count=vertical&v=1&url="+b+"' and ua='#Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/33.0.1750.154 Safari/537.36'",format:"xml",env:"http://datatables.org/alltables.env"},success:function(f){var e=$(f).find("content").text();var c=e.match(/<em id="cnt">(\d+)<\/em>/i);var d=(c!==null)?c[1]:0;$(a).text(d);}});}function getAllSocialCounts(a){$(function(){get_social_count_twitter(a,".twitter-count");get_social_count_facebook(a,".facebook-count");get_social_count_googleplus(a,".googleplus-count");get_social_count_hatebu(a,".hatebu-count");get_social_count_pocket(a,".pocket-count");});}